$pinkGr: linear-gradient(180deg, #BC9CFF 0%, #8BA4F9 100%);
$pinkC: rgba(188, 156, 255, 1);
$greenGr: linear-gradient(180deg, #6FCF97 0%, #66D2EA 100%);
$greyTx: rgba(31, 33, 65, 0.502);
$darkTx: rgba(31, 32, 65, 0.75);

@mixin button($left) {
    position: absolute;
    width: 44px;
    height: 40px;
    left: $left;
    top: 30px;
    align-items: center;
    text-align: center;
    background-color: white;
    border: 0px;
    border-radius: 4px;
    cursor: pointer;
    background-image: url("../src/img/dropd.svg");
    background-repeat: no-repeat;
    background-position: center;
    
        &:hover{
        background-color: hsl(0, 0%, 97%);
    }
}

body {
    font-family: 'Montserrat', sans-serif;    
    font-size: 14px;
    color: $greyTx;
}
.logo{
    width: 106px;
    height: 40px;
    padding-left: 140px;
    align-items: center;
    
}
.header {
    background-color: white;
    display: flex;
    box-shadow: 0px 10px 20px rgba(31, 32, 65, 0.05);
    height: 70px;
    max-width: 100;
    align-items: center;
    max-width: 1440px;
    min-width: 1440px;
    margin: 0px auto;
    
}
.nav {
    display: flex;
    list-style: none;
    height: 17px;
    margin-left: 247px;
        &-list {
        margin-right: 20px;
        margin-left: 0px;
        cursor:default;
    }
        &-item {
        text-decoration: none;
        color: $greyTx;
        
        }
}
.pinkB {
    background:$pinkGr;
    color: white;
    height: 34px;
    width: 196px;
    border-radius: 22px;
    border-style: none;
    font-size: 12px;
