    cursor: pointer;
    outline: none;
}
.framePink {
    background:$pinkGr;
    height: 34px;
    width: 87px;
    border: 2px;
    border-radius: 22px;
    margin-right: 20px;
    
}
.whiteB {
    background-color: white;
    color: $pinkC;
    font-size: 12px;
    font-weight: 700;
    border: none;
    border-radius: 22px;
    height: 30px;
    width: 83px;
    margin: 2px 2px;
    cursor: pointer;
    outline: none;
}

.drop {
    position: absolute;
    background: rgba(188, 156, 255, 0.02);
    padding-left: 0px;
    padding-top: 10px;
    
    background-color: white;
    &-link {
        margin-top: 10px;
        display: none;
        padding-top: 15px;
        
    }
}
li.nav-list:hover li.drop-link{
    display: block; 
    background-color: #fff;
    padding: 10px;
}
.down {
    background-image: url(../src/img/dropd.svg);
    background-repeat: no-repeat;
    width: 70px;
    background-position: right;
    padding-left: 1px;
    margin-left: 4px;
}
.wrapper {
    
    background-image: url(../src/img/mainBG.jpg);
    background-position: center 100%;
    width: 1440px;
    height: 830px;
    background-repeat: no-repeat;
    margin: 0px auto;
    box-shadow: 0px 10px 20px rgba(31, 32, 65, 0.05);
        
}
